/*

### 3.2. PUSH_DATA packet ###

That packet type is used by the gateway mainly to forward the RF packets 
received, and associated metadata, to the server.

 Bytes  | Function
:------:|---------------------------------------------------------------------
 0      | protocol version = 2
 1-2    | random token
 3      | PUSH_DATA identifier 0x00
 4-11   | Gateway unique identifier (MAC address)
 12-end | JSON object, starting with {, ending with }, see section 4

*/

function getRandomIntInclusive(min, max) {
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min + 1)) + min; //The maximum is inclusive and the minimum is inclusive 
}

var json = msg.payload;

var jsonStr = JSON.stringify(json);


const jsonBuf = Buffer.from(jsonStr);

var len = jsonStr.length;

var resBuf = Buffer.allocUnsafe(12+len);

var macBuf = Buffer.from(msg.mac, "hex");

resBuf[0] = 2;
resBuf[1] = getRandomIntInclusive(0, 255);
resBuf[2] = getRandomIntInclusive(0, 255);
resBuf[3] = 0x00;

macBuf.copy(resBuf,4,0,8);

jsonBuf.copy(resBuf,12,0,len);

msg.payload = resBuf
return msg;

var p = msg.payload;
var rx = p.rxInfo;

var rxpk = {
		"time":rx.time,
		"tmst":rx.timestamp,
		"chan":rx.channel,
		"rfch":rx.rfChain,
		"freq":rx.frequency / 1000000.0,
		"stat":rx.crcStatus,
		"modu":rx.dataRate.modulation,
		"datr":"SF"+rx.dataRate.spreadFactor+"BW"+rx.dataRate.bandwidth,
		"codr":rx.codeRate,
		"rssi":rx.rssi,
		"lsnr":rx.loRaSNR,
		"size":rx.size,
		"data": p.phyPayload
	};

msg.mac = rx.mac;
msg.payload = { rxpk:[ rxpk ] };

return msg;

const TTN_NETID = 0x13;
const MType_Unconfirmed_Data_Up = 0x40;
const MType_Confirmed_Data_Up = 0x80;

var p = msg.payload;

var phyPayload = Buffer.from(p.phyPayload,'base64');

if(phyPayload.length > 5) {
    var hdr = phyPayload[0];
    var netid = phyPayload[4] >>> 1;
    if(((hdr === MType_Unconfirmed_Data_Up) || (hdr === MType_Confirmed_Data_Up)) && (netid === TTN_NETID)) {
        return msg;
    }
}
return null;

// Process packet sent by the TTN LoRa Network Server
// https://github.com/Lora-net/packet_forwarder/blob/master/PROTOCOL.TXT

// ### 3.2. PUSH_DATA packet ###
// That packet type is used by the gateway mainly to forward the RF packets 
// received, and associated metadata, to the server.
const PUSH_ACK = 0x01;

// ### 5.3. PULL_ACK packet ###
// That packet type is used by the server to confirm that the network route is 
// open and that the server can send PULL_RESP packets at any time.
const PULL_ACK = 0x04;

// ### 5.4. PULL_RESP packet ###
// That packet type is used by the server to send RF packets and associated 
// metadata that will have to be emitted by the gateway.
const PULL_RESP = 0x03;


return msg;
